contracts/config/FileStorage.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

contract FileStorage {
    // Mapping to store file hashes (off-chain) linked to Ethereum addresses
    mapping(address => bytes32) private fileHashes;

    // Event emitted when a new file hash is stored
    event FileStored(address indexed user, bytes32 fileHash);

    // Function to store a file hash
    function storeFileHash(bytes32 _fileHash) external {
        fileHashes[msg.sender] = _fileHash;
        emit FileStored(msg.sender, _fileHash);
    }

    // Function to retrieve a file hash
    function getFileHash() external view returns (bytes32) {
        return fileHashes[msg.sender];
    }
}

contracts/SecureEscrow.sol.content:
// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.0;

interface IERC20 {
    function transfer(
        address recipient,
        uint256 amount
    ) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);

    function balanceOf(address account) external view returns (uint256);
}

contract SecureEscrow {
    address public immutable usdtToken;
    address public immutable gnosisSafe;

    mapping(address => uint256) public ethBalances;
    mapping(address => uint256) public usdtBalances;

    event Deposited(address indexed user, uint256 amount, string currency);
    event Withdrawn(address indexed user, uint256 amount, string currency);

    modifier onlySafe() {
        require(
            msg.sender == gnosisSafe,
            'Not authorized: Only Safe can approve'
        );
        _;
    }

    constructor(address _usdtToken, address _gnosisSafe) {
        usdtToken = _usdtToken;
        gnosisSafe = _gnosisSafe;
    }

    // Deposit ETH or XDC  into escrow
    function depositETH() external payable {
        require(msg.value > 0, 'Must send ETH');
        ethBalances[msg.sender] += msg.value;
        emit Deposited(msg.sender, msg.value, 'ETH');
    }

    // Deposit USDT into escrow
    function depositUSDT(uint256 amount) external {
        require(amount > 0, 'Must send USDT');
        require(
            IERC20(usdtToken).transferFrom(msg.sender, address(this), amount),
            'USDT Transfer failed'
        );
        usdtBalances[msg.sender] += amount;
        emit Deposited(msg.sender, amount, 'USDT');
    }

    // Withdraw ETH (requires Gnosis Safe approval)
    function withdrawETH(address recipient, uint256 amount) external onlySafe {
        require(ethBalances[recipient] >= amount, 'Insufficient ETH balance');
        ethBalances[recipient] -= amount;
        payable(recipient).transfer(amount);
        emit Withdrawn(recipient, amount, 'ETH');
    }

    // Withdraw USDT (requires Gnosis Safe approval)
    function withdrawUSDT(address recipient, uint256 amount) external onlySafe {
        require(usdtBalances[recipient] >= amount, 'Insufficient USDT balance');
        usdtBalances[recipient] -= amount;
        require(
            IERC20(usdtToken).transfer(recipient, amount),
            'USDT Transfer failed'
        );
        emit Withdrawn(recipient, amount, 'USDT');
    }

    // Get total contract balance (ETH & USDT)
    function getEscrowBalance()
        external
        view
        returns (uint256 ethBalance, uint256 usdtBalance)
    {
        ethBalance = address(this).balance;
        usdtBalance = IERC20(usdtToken).balanceOf(address(this));
    }

    // Get user balance (ETH & USDT)
    function getUserBalance(
        address user
    ) external view returns (uint256 ethBalance, uint256 usdtBalance) {
        ethBalance = ethBalances[user];
        usdtBalance = usdtBalances[user];
    }
}

contracts/TokenLockContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';

contract TokenLockContract is AccessControl, ReentrancyGuard {
    bytes32 public constant ADMIN_ROLE = keccak256('ADMIN_ROLE');
    bool public paused;

    //Transfer Request Struct to track the request raised by Users in platform
    struct TransferRequest {
        address from;
        address to;
        uint256 amount;
        uint256 fee;
        bool approved;
        bool claimed;
    }

    mapping(string => address) public tokenMapping;
    mapping(uint256 => TransferRequest) public transferRequests;
    uint256 public requestCounter;
    uint256 public transferFee; // Fixed fee for transfers

    event TransferRequested(
        uint256 requestId,
        address indexed from,
        address indexed to,
        uint256 amount,
        uint256 fee
    );
    event TransferApproved(uint256 requestId);
    event TokensClaimed(uint256 requestId);
    event TransferFeeSetup(address indexed from);
    event WithdrawFee(address indexed from, uint256 amount);

    modifier whenNotPaused() {
        require(!paused, 'Withdrawals are paused');
        _;
    }

    function setPaused(bool _paused) external onlyRole(ADMIN_ROLE) {
        paused = _paused;
    }

    constructor(uint256 fee) {
        transferFee = fee;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(ADMIN_ROLE, msg.sender);
    }

    function setTokenMapping(
        string memory _prefix,
        address _to
    ) public onlyRole(ADMIN_ROLE) {
        tokenMapping[_prefix] = _to;
    }

    //Request Transfer
    function requestTransfer(
        address to,
        uint256 amount,
        string memory _prefix
    ) external {
        require(to != address(0), 'Invalid recipient');
        require(amount > 0, 'Amount must be greater than zero');
        address _token = tokenMapping[_prefix];
        require(
            IERC20(_token).balanceOf(msg.sender) >= amount,
            'Insufficient Balance'
        );

        // Transfer tokens to this contract
        require(
            IERC20(_token).transferFrom(msg.sender, address(this), amount),
            'Token transfer failed'
        );

        transferRequests[requestCounter] = TransferRequest({
            from: msg.sender,
            to: to,
            amount: amount,
            fee: 0,
            approved: false,
            claimed: false
        });

        emit TransferRequested(requestCounter, msg.sender, to, amount, 0);
        requestCounter++;
    }

    function approveTransfer(uint256 requestId) external onlyRole(ADMIN_ROLE) {
        TransferRequest storage request = transferRequests[requestId];
        require(!request.approved, 'Request already approved');
        require(!request.claimed, 'Tokens already claimed');
        request.approved = true;
        emit TransferApproved(requestId);
    }

    function claimTokens(uint256 requestId, string memory _prefix) external {
        TransferRequest storage request = transferRequests[requestId];
        require(request.approved, 'Request not approved');
        require(!request.claimed, 'Tokens already claimed');
        require(msg.sender == request.to, 'Not the recipient');
        address _token = tokenMapping[_prefix];

        request.claimed = true;
        // Transfer tokens to the recipient
        require(
            IERC20(_token).transfer(msg.sender, request.amount),
            'Token transfer failed'
        );
        emit TokensClaimed(requestId);
    }

    function withdrawFees(
        uint256 _amount
    ) external onlyRole(ADMIN_ROLE) whenNotPaused nonReentrant {
        require(_amount <= address(this).balance, 'Insufficient balance');
        payable(msg.sender).transfer(_amount);
        emit WithdrawFee(msg.sender, _amount);
    }

    function setTransferFee(uint256 fee) external onlyRole(ADMIN_ROLE) {
        transferFee = fee;
        emit TransferFeeSetup(msg.sender);
    }
}

